import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Table } from 'react-bootstrap';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    useRouteMatch,
    Link
  } from "react-router-dom";



let api_url = "http://localhost:4000/campaigns";

function CampaignList() {
    const [data, setData] = useState([])

   
    useEffect(() => {
        const fetchData = async () => {
            const result = await fetch(api_url).then(response => {
                return response.json()
            })
            setData(result)
            console.log(result, "re")
        }
        fetchData()
    }, [])

    handleButtonClick = () => {
        //calculate your data here
        //then redirect:
        this.context.router.push({ //browserHistory.push should also work here
          pathname: pathToMyComponent,
          state: {yourCalculatedData: data}
        }); 
      }

    return (
       
        <div>
             
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Campaigns Name</th>
                    </tr>
                </thead>
                <tbody>
                    {(data.campaigns || []).map((campaigns) => {
                        return (
                            <tr onClick={handleButtonClick}>
                                <td><Link to={`${match.url}/dashboard`}>{campaigns.id}</Link></td>
                                <td>{campaigns.name}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </Table>
        </div>
    );
}

export default CampaignList;
