import React, { useState, useEffect, useRef } from "react";
// import 'bootstrap/dist/css/bootstrap.min.css';
// import { Table,Container, Row, Col } from 'react-bootstrap';

import { useDispatch, useSelector } from "react-redux";
import { getCampaignDetails } from "../store/actions/campaign";
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
// import StarBorderIcon from '@material-ui/icons/StarBorder';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 500,
    height: 450,
    // backgroundColor: 'red',
  },
}));
function Dashboard(props) {
  
  const classes = useStyles();
  
  const campaignId = props.location.campaigns.id;
  const campaign = props.location.campaigns;
  let api_url = "http://localhost:4000/campaigns";

  //TODO : for store
//   const dispatch = useDispatch();
//   const [campaignDetails, setCampaign] = useState();
//   const campaignData = useSelector((state) => state.campaignState.campaignDetails)
//   useEffect(() => {
//     if (campaignId) {
//         dispatch(getCampaignDetails(campaign, 0))
//     }
// }, [campaignDetails, dispatch])


const [data, setData] = useState([])
useEffect(() => {
        const fetchData = async (num) => {
            const result = await fetch(api_url+'/'+campaignId+'?number='+num).then(response => {
              console.log(response);
                return response.json()
            }) .catch(err => {
                throw new Error(err)
              })
            setData(result)
        }
        let num = 0;
        fetchData(num);
        const interval = setInterval(() => {
          num++;
          fetchData(num)
        }, 5000);
        return ()=>{ clearInterval(interval)}
    }, [])

  return (
    <div className={classes.root}>
    <GridList className={classes.gridList} cols={2.5}>
      {console.log(data)}
        <GridListTile>
            <GridListTileBar
              title={data.impressions}
              classes={{
                root: classes.titleBar,
                title: classes.title,
              }}
              actionIcon={
                <IconButton aria-label={`star ${data.impressions}`}>
                </IconButton>
              }
            />
        </GridListTile>
    </GridList>
  </div>
  );
}

export default Dashboard;
