import React, { Component } from "react";
import PropTypes from "prop-types";
import {
  Card,
  CardContent,
  CardActions,
  Typography,
  CardMedia,
  withStyles
} from "@material-ui/core";

const styles = theme => ({
  card: {
    display: "block",
    margin: 5,
    [theme.breakpoints.up("sm")]: {
      display: "flex"
    },
    borderRadius: 0
  },

  caption: {
    textTransform: "uppercase"
  },

  title: {
    textTransform: "uppercase",
    color: theme.palette.primary.main,
    fontWeight: "bold"
  },

  media: {
    width: "auto",
    height: 300,
    [theme.breakpoints.up("sm")]: {
      width: 1500
    },
    flexBasis: "50%"
  },

  content: {
    display: "flex",
    flexDirection: "column",
    flexBasis: "50%",
    backgroundColor: "#eee"
  },

  cardText: {
    flex: "1 0 auto",
    marginBottom: 15,
    [theme.breakpoints.up("sm")]: {
      marginBottom: 0
    }
  },

  linkAction: {
    textDecoration: "none",
    textTransform: "uppercase",
    color: theme.palette.primary.main,
    fontWeight: "bold"
  },
  MuiGridContainer: {
    width: '100%',
    display: 'flex',
    flexWrap: 'wrap',
    boxSizing:'border-box',
  }

});

class CardComponent extends Component {
  render() {
    const { classes, imageUrl, title, caption, value, actionLink } = this.props;

    return (
        <div className="MuiGridContainer">
        <Card className={classes.card}>
            
          <CardContent className={classes.content}>
            <div className={classes.cardText}>
              <Typography variant="h6" className={classes.title} gutterBottom>
                {title}
              </Typography>
              <Typography variant="body1">{value}</Typography>
            </div>
          </CardContent>
         
        </Card>
         </div>
      );
    }
  }
CardComponent.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(CardComponent);



