import React, { useState, useEffect, useRef } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Table,Container, Row, Col } from 'react-bootstrap';

import { useDispatch, useSelector } from "react-redux";
import { getCampaignDetails } from "../store/actions/campaign";
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
// import StarBorderIcon from '@material-ui/icons/StarBorder';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 500,
    height: 450,
  },
}));
function Dashboard(props) {
  
  const classes = useStyles();
  
  const campaignId = props.location.campaigns.id;
  const campaign = props.location.campaigns;
  let api_url = "http://localhost:4000/campaigns";

  //TODO : for store
//   const dispatch = useDispatch();
//   const [campaignDetails, setCampaign] = useState();
//   const campaignData = useSelector((state) => state.campaignState.campaignDetails)
//   useEffect(() => {
//     if (campaignId) {
//         dispatch(getCampaignDetails(campaign, 0))
//     }
// }, [campaignDetails, dispatch])


const [data, setData] = useState([])
useEffect(() => {
        const fetchData = async () => {
            const result = await fetch(api_url+'/'+campaignId+'?number='+0).then(response => {
              console.log(response);
                return response.json()
            }) .catch(err => {
                throw new Error(err)
              })
            setData(result)
        }
        fetchData()
    }, [])

    

  return (
    <div>
      <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Campaigns Name</th>

                    </tr>
                </thead>
                <tbody>
                            <tr>
                                <td>
                                   {'wwww'}
                                </td>
                                <td>
                                   {'wwww'}
                                </td>
                            </tr>
                </tbody>
            </Table>
        
        <Container>
  <Row>
    <Col>1 of 2</Col>
    <Col>2 of 2</Col>
  </Row>
  <Row>
    <Col>1 of 3</Col>
    <Col>2 of 3</Col>
    <Col>3 of 3</Col>
  </Row>
</Container>
        </div>
  );
}

export default Dashboard;
