import React, { useState, useEffect, useRef, usePrevious } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCampaignDetails } from "../store/actions/campaign";
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
var dataImp=[0]
var dataClicks=[0]
var dataUsers=[0]

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    margin:'10px',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 500,
    height: 450,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
    textAlign:'center',
    fontWeight:600
  },
}));
const Dashboard=(props) =>{
  const classes = useStyles();
  
  const campaignId = props.location.campaigns.id;
  const campaign = props.location.campaigns;
  let api_url = "http://localhost:4000/campaigns";

  //TODO : for store
//   const dispatch = useDispatch();
//   const [campaignDetails, setCampaign] = useState();
//   const campaignData = useSelector((state) => state.campaignState.campaignDetails)
//   useEffect(() => {
//     if (campaignId) {
//         dispatch(getCampaignDetails(campaign, 0))
//     }
// }, [campaignDetails, dispatch])

const [data, setData] = useState([])

useEffect(() => {
        const fetchData = async (num) => {
        
            const result = await fetch(api_url+'/'+campaignId+'?number='+num).then(response => {
                return response.json()
            }) .catch(err => {
                throw new Error(err)
              })
            setData(result);
        }
        let num = 0;
        fetchData(num);
        const interval = setInterval(() => {
          num++;
          fetchData(num)
       
        }, 5000);
        return ()=>{ clearInterval(interval)}
    }, []);

    
    const calculateCtr = (impressions, clicks)=> {
      return (
        (clicks/impressions) * 100
        )
    }
    const totalImp=()=>{
        if(data.impressions) { dataImp.push(data.impressions)};
          var sum = dataImp.reduce(function(prev,curr){
            return curr + prev;
        },0);
      return sum;
    }
    const totalClicks=()=>{
        if(data.clicks) { dataClicks.push(data.clicks)};
          var sum = dataClicks.reduce(function(prev,curr){
            return curr + prev;
        },0);
      return sum;
    }
    const totalUsers=()=>{
        if(data.users) { dataUsers.push(data.users)};
          var sum = dataUsers.reduce(function(prev,curr){
            return curr + prev;
        },0);
      return sum;
    }
    // const totalImp = (data)=> {
    //   temp.push(data.impressions);
    //   console.log(temp)
    //   return temp
    // }

  return (
    <div className={classes.root}>
      <Card className={classes.root} variant="outlined">
      <CardContent>
        <Typography variant="h5" component="h2">
         Total impressions        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          {totalImp()}
        </Typography>
      </CardContent>
      </Card>
      <Card className={classes.root} variant="outlined">
      <CardContent>
        <Typography variant="h5" component="h2">
         Total clicks 
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          {totalClicks()}
        </Typography>
      </CardContent>
      </Card>
     
      <Card className={classes.root} variant="outlined">
      <CardContent>
        <Typography variant="h5" component="h2">
         Total users 
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          {totalUsers()}
        </Typography>
      </CardContent>
      </Card>
      <Card className={classes.root} variant="outlined">
      <CardContent>
        <Typography variant="h5" component="h2">
          Most recent impressions
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          {data.impressions}
        </Typography>
      </CardContent>
    </Card>
      <Card className={classes.root} variant="outlined">
      <CardContent>
        <Typography variant="h5" component="h2">
          Most recent clicks
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          {data.clicks}
        </Typography>
      </CardContent>
    </Card>
    <Card className={classes.root} variant="outlined">
      <CardContent>
        <Typography variant="h5" component="h2">
          CTR
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          {calculateCtr(data.impressions,data.clicks)}
        </Typography>
      </CardContent>
    </Card>
    <Card className={classes.root} variant="outlined">
      <CardContent>
        <Typography variant="h5" component="h2">
          Most recent users
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          {data.users}
        </Typography>
      </CardContent>
    </Card>
     </div>
  );
}

export default Dashboard;
